package Homeworks;

public class chararray {

	public static void main(String[] args) {
																// TODO Auto-generated method stub
		char [][] p= {{'a','g','k'},{'p','q','r'}};
		 for(char i=0;i<p.length;i++)
       {
    	   for(char j=0;j<p[0].length;j++)
    	   {
    		   System.out.print(p[i][j]+" ");
    	   }
		   System.out.println();
       }
    	  
	}

}
